import {Component, Input, OnChanges, OnInit, SimpleChanges} from '@angular/core';
import {Movie} from "../../models/movie";

@Component({
  selector: 'app-movie-list',
  templateUrl: './movie-list.component.html',
  styleUrls: ['./movie-list.component.css']
})
export class MovieListComponent implements OnInit, OnChanges {

  @Input() filter: string;

  static_list: Movie[] = [
    {name: 'batman begins', img: 'data:image/jpeg;base64,'},
    {name: 'batman dark knight', img: 'https://upload.wikimedia.org/wikipedia/pt/d/d1/The_Dark_Knight.jpg'},
    {name: 'batman dark knight rises', img: 'https://a.ltrbxd.com/resized/film-poster/1/6/5/5/0/16550-the-dark-knight-rises-0-230-0-345-crop.jpg?k=38a6974b66'},
    {name: 'once upon a time in hollywood', img: 'https://upload.wikimedia.org/wikipedia/en/thumb/a/a6/Once_Upon_a_Time_in_Hollywood_poster.png/220px-Once_Upon_a_Time_in_Hollywood_poster.png'},
    {name: 'avengers infinity war', img: 'https://upload.wikimedia.org/wikipedia/pt/9/90/Avengers_Infinity_War.jpg'},
    {name: 'parasite', img: 'data:image/jpeg;base64,'}
  ];

  list: Movie[] = [];

  constructor() { }

  ngOnInit(): void {
    this.list = this.static_list;
  }

  ngOnChanges(changes: SimpleChanges): void {
    this.list = this.static_list.filter(
      (m: Movie) => { return m.name.includes(this.filter); }
      );
  }

}
